<?php/** * The sidebar containing the above the footer widget area. * * If no active widgets in sidebar, let's hide it completely. * * @package WordPress * @subpackage SocialChef * @since SocialChef 1.0 */?><?php if ( is_active_sidebar( 'above-footer' ) ) : ?>	<div id="above-footer-sidebar" class="above-footer-sidebar sidebar widget-area " role="complementary">		<ul>		<?php dynamic_sidebar( 'above-footer' ); ?>		</ul>	</div><!-- #secondary --><?php endif; ?>