<?php/** * The sidebar containing the right widget area. * * If no active widgets in sidebar, let's hide it completely. * * @package WordPress * @subpackage SocialChef * @since SocialChef 1.0 */?><?php if ( is_active_sidebar( 'right' ) ) : ?>	<aside id="secondary-right" class="right-sidebar sidebar widget-area one-fourth" role="complementary">		<ul>		<?php dynamic_sidebar( 'right' ); ?>		</ul>	</aside><!-- #secondary --><?php endif; ?>